                      <Sparkles className="w-7 h-7 text-[#c3ff00]" />
                      <span className="font-black text-[22px] text-[#c3ff00] tracking-wider">
                        BRILLIANCE MEMBER
                      </span>
                    </div>
                  </motion.div>
                </div>
              </motion.div>
            </div>
          )}

          {/* Screen 4: Wallet cards */}
          {screen.type === "wallet-cards" && (
            <div className="flex-1 flex flex-col px-6 pt-16 pb-32">
