import { createContext, useContext, ReactNode, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { useTelegram } from './TelegramProvider';
import { apiRequest } from '@/lib/queryClient';
import type { User } from '@shared/schema';

interface AuthContextType {
  user: User | null;
  isLoading: boolean;
  isAuthenticated: boolean;
  login: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType>({
  user: null,
  isLoading: true,
  isAuthenticated: false,
  login: async () => {},
});

export function AuthProvider({ children }: { children: ReactNode }) {
  const { initData, isReady } = useTelegram();
  const queryClient = useQueryClient();

  const { mutateAsync: loginMutation } = useMutation({
    mutationFn: async () => {
      const response = await apiRequest<{ user: User; token: string }>('/api/auth/login', {
        method: 'POST',
        body: JSON.stringify({ initData }),
      });
      localStorage.setItem('authToken', response.token);
      return response;
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/auth/me'] });
    },
  });

  const { data: userData, isLoading } = useQuery<{ user: User }>({
    queryKey: ['/api/auth/me'],
    enabled: isReady && !!localStorage.getItem('authToken'),
  });

  useEffect(() => {
    if (isReady && initData && !localStorage.getItem('authToken')) {
      loginMutation();
    }
  }, [isReady, initData]);

  const login = async () => {
    await loginMutation();
  };

  const value: AuthContextType = {
    user: userData?.user || null,
    isLoading,
    isAuthenticated: !!userData?.user,
    login,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export const useAuth = () => useContext(AuthContext);

server/utils/jwt.ts.content:
import jwt from 'jsonwebtoken';
import { randomBytes } from 'crypto';

const isDevelopment = process.env.NODE_ENV === 'development';

let JWT_SECRET: string;

if (process.env.JWT_SECRET) {
  JWT_SECRET = process.env.JWT_SECRET;
} else if (isDevelopment) {
  JWT_SECRET = randomBytes(32).toString('hex');
  console.warn('\nâš ï¸  WARNING: JWT_SECRET not set. Using randomly generated secret for development.');
  console.warn('âš ï¸  This will invalidate all tokens on server restart.');
  console.warn('âš ï¸  Set JWT_SECRET environment variable for production.\n');
} else {
  throw new Error('FATAL: JWT_SECRET environment variable must be set in production');
}

const JWT_EXPIRES_IN = '7d';

export interface JWTPayload {
  userId: string;
  telegramId: string;
}

export function generateToken(payload: JWTPayload): string {
  return jwt.sign(payload, JWT_SECRET, { expiresIn: JWT_EXPIRES_IN });
}

export function verifyToken(token: string): JWTPayload | null {
  try {
    const decoded = jwt.verify(token, JWT_SECRET) as JWTPayload;
    return decoded;
  } catch (error) {
    return null;
  }
}

